s = "aaaackfkcaaaa"


def validPalindrome(s):
    i = 0
    j = len(s) - 1
    while i <= j:
        if s[i] == s[j]:
            i += 1
            j -= 1
        # 같지 않으면 비교를 한다.
        else:
            print(i,j)
            print(s[i:j])
            print(s[i:j][::-1])
            print(s[i+1:j+1])
            print(s[i+1:j+1][::-1])
            return s[i:j] == s[i:j][::-1] or s[i+1:j+1] == s[i+1:j+1][::-1]
    return True

print(validPalindrome(s))